import React from "react";
import './style.css'

const InputField = () => {
	return (
		<form className="input">
			<input	type="input" 
					placeholder="Enter a task" 
					className="input_box"
			/>
			<button className="input_submit">
				Go
			</button>
		</form>
	);
}

const App:React.FC = () =>
{
	const [todo, setTodo] = useState<string>("");
	return (
		<div className="App">
			<span className="heading">Todo's</span>
			<InputField/>
		</div>
		);
}

export default App;
